function [indices,n] = FindRandomRec(n,list2)% [indices,n] = FindRandomRec(n,list2)%% Choose n random receptors from list2.  Each% receptor is chosen at most once.  If there% are fewer receptors requested than are in the% list, return only the number available.%% 7/10/94		dhb		Added comments.% Check size[n2,null] = size(list2);if (n2 < n)  n = n2;end% Go through list, choosing and removing.list2left = list2;n2left = n2;for i = 1:n  % Pick a member and store its index  memberOf2 = list2left(Ranint(n2left),:);  theIndex = find( list2(:,1) == memberOf2(1) & ...                   list2(:,2) == memberOf2(2) );  indices(i) = theIndex(1);  % Remove target from list of those left  indicesleft = find( list2left(:,1) ~= memberOf2(1) | ...                      list2left(:,2) ~= memberOf2(2) );  n2left = n2left - 1;  if (n2left == 0)    break;  end  list2left = list2left(indicesleft,:);   end  